$colors: (
    backgroundCol: #fff,
    color: #222,
    placeholder: #33333380,
    titlesColor: #0097e6
);

@function color($colorName) {
    @return map-get($colors, $colorName );
};

// Fonts
$titleFont: "Montserrat Black";
$mainFont: "Montserrat Medium";
$textFont: "Montserrat Bold";

// Reset margin and padding
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    button {
        width: 100px;
        padding: 5px;
        margin: 3px 0;
        background: none;
        border: 1px solid rgba(0,0,0,0.3);
        border-radius: 2px;
        cursor: pointer;
        outline: none;

        font-family: $mainFont;
        color: color(color);
        background: color(backgroundCol);
        transition: 0.2s ease;
    }

    button:hover {
        border: 1px solid rgba(0,0,0,0.6);
        color: color(backgroundCol);
        background: color(color);
    }

    input[type='color'] {
        display: block;
        text-align: left;
        background: none;
        border: none;
        width: 110px;
        font-size: 14px;
        cursor: pointer;
        outline: none;
    }
    #colorPalette::before {
        font-family: $mainFont;
        content: "Custom color";
    }

    #backgroundColor::before {
        font-family: $mainFont;
        content: "Custom color"
    }

    #colorPalette:hover::before, #backgroundColor:hover::before {
        color: #e1302a;
    }
    input[type='color']::-webkit-color-swatch-wrapper {
        opacity: 0;
    }
}

.container {
    height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: color(backgroundCol);

}

