    display: none;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    padding: 10px;
    text-align: center;
    width: 200px;
    background-color: color(backgroundCol);
    z-index: 9;

    #show-warning {
        display: none;
    }

    * {
        margin: 5px 0;
    }

    h3 {
        font-family: $titleFont;
        color: color(titlesColor);
    }

    p {
        text-align: center;
        font-size: 12px;
        color: #e1302a;
        font-family: $mainFont;
    }

    .input-fields {
        display: flex;
        justify-content: space-between;
        align-items: center;
        width: 100%;

        input {
            width: 80px;
            padding: 5px;
            border: 1px solid rgba(0,0,0,0.4);
            outline-color: #0097e6;
        }
    }

    button {
        width: 60px;
    }
}

.new-project_window.active {
    border: 1px solid rgba(0,0,0,0.3);
    width: 250px;
}

.main-menu {
    display: block;
    width: 250px;
    // background-color: red;
    text-align: center;
    padding: 10px;

    h1 {
        font-family: "Fredoka One";
        letter-spacing: 3px;
        font-size: 3rem;
        color: color(titlesColor);
        animation: titleColor 5s ease forwards infinite;
        position: relative;

        i {
            font-size: 60px;
            position: absolute;
            left: 56px;
            top: -10px;
            transform: rotate(140deg);
            animation: brushAnimation 5s ease forwards infinite;
        }
    }

    p {
        font-family: "Montserrat SemiBold";
        font-style: italic;
