            }
        }

        #current-bgColor { 
            color: #555;
        }

        // Download drawing
        #saveDrawing {
            display: inline-block;
            border: 1px solid rgba(0,0,0,0.3);
            padding: 3px;
            cursor: pointer;
            text-decoration: none;

            font-family: $textFont;
            text-transform: uppercase;
            font-size: 0.8rem;
            color: color(titlesColor);
            background-color: color(backgroundCol);
            transition: 0.2s ease;
        }
        #saveDrawing:hover {
            color: color(backgroundCol);
            background-color: color(titlesColor);
        }

        // Checkboxes for cap and line types
        .cap-line_type {
            display: flex;
            justify-content: space-between;
            align-items: center;

            input {
                display: inline-block;
                width: 15px;
                height: 15px;
            }

            img {
                display: inline-block;
                padding: 0;
                margin: 0;
                margin-right: -5px;
            }
        }

        // CUSTOM CHECKBOX
        .switch {
            position: relative;
            display: block;
            width: 30px;
            height: 30px;

            .slider {
                position: absolute;
                cursor: pointer;
                top: 0; left: 0; bottom: 0; right: 0;
                transition: 0.3s ease;
                padding: 0;
            }
            input {
                display: none;
            }
            .slider:before {
                position: absolute;
                top: 0px;
                left: 0px;
                transition: 0.3s ease;
                font-size: 10px;
                font-family: $textFont;
                color: #fff;
                width: 28px;
                height: 24px;

                display: flex;
                justify-content: center;
                align-items: center;
            }

            // input:checked + .slider {
            //     transform: rotate(45deg);
            // }

            input:checked + .slider:before {
                color: #fff;
                content: "ON";
                background-color: #0097e6;
            }

            input:not(:checked) + .slider:before {
                color: #fff;
                content: "OFF";
                background-color: #e1302a;
            }
        }

        .eraser {
            align-items: center;
        }

        #clear-all_container {
            display: flex;
            justify-content:flex-start;
            align-items: center;

            i {
                margin-left: 10px;
                color: color(placeholder);
                cursor: pointer;
                transition: 0.2s ease;
            }

            i:hover {
                transform: translateY(-2px);
                color: #e1302a;
            }

        }

        #canvas-size {
            margin: 0 0 10px 0;
            width: 150px;
        }

        .undo-redo_container {
            display: flex;
            align-items: center;
            justify-content: space-between;
            margin: 10px 0 0 0;
            
            button {
                width: 40px;
                padding: 0px;
                margin-right: 3px;

                display: flex;
                justify-content: center;
                align-items: center;

                i {
                    font-size: 22px;
                }
            }
        }

        // COLOR BOXES
        .color-boxes_container {
            display: flex;
            justify-content: flex-start;
            align-items: flex-start;
            flex-wrap: wrap;

            width: 202px;
            height: 52px;
            border: 1px solid rgba(0,0,0,0.2);

            .color-box {
                width: 25px;
                height: 25px;
                margin: 0;
                padding: 0;
                cursor: pointer;
            }
        }

        .controls-bottom {
            position: absolute;
            left: 0;
            bottom: 0;
            width: 100%;
            height: 40px;
            margin: 0;
            border-top: 1px solid rgba(0,0,0,0.2);

            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 5px;
            
            #exit {
                cursor: pointer;
                font-family: "Montserrat SemiBold";
                font-size: 0.85rem;
                transition: 0.2s ease;
            }
            #exit:hover {
                color: #e1302a;
            }

            #info {
                color: color(titlesColor);
                cursor: pointer;
                transition: 0.2s ease;
                
                i {
                    font-size: 28px;
                }
            }
            #info:hover {
                color: #0173b1
            }
        }
    }
    .controlsActive {
        transform: translateX(-250px);

        .burger-menu {
            right: -70px;
        }
    }
}

